use std::io::Read;
use std::cmp::min;
use std::collections::VecDeque;
use std::iter::FromIterator;

fn main() {
    let mut buf = String::new();
    std::io::stdin().read_to_string(&mut buf).unwrap();

    let answer = solve(&buf);

    println!("{}", answer);
}

fn solve(input: &str) -> String {
    let mut iterator = input.split_whitespace();

    let _n: usize = iterator.next().unwrap().parse().unwrap();
    let mut s: VecDeque<char> = VecDeque::from_iter(iterator.next().unwrap().chars());
    while !s.is_empty() && *s.front().unwrap() == '.' {
        s.pop_front().unwrap();
    }
    while !s.is_empty() && *s.back().unwrap() == '#' {
        s.pop_back().unwrap();
    }
    let s: Vec<char> = Vec::from(s);

    let n: usize = s.len();
    if n == 0 {
        return "0".to_string();
    }

    let mut s1 = s.clone();
    let mut c1: usize = 0;
    for i in 0..(n - 1) {
        if s1[i] == '#' && s1[i + 1] == '.' {
            s1[i + 1] = '#';
            c1 += 1;
        }
    }

    let mut s2 = s.clone();
    let mut c2: usize = 0;
    for i in (1..n).rev() {
        if s2[i - 1] == '#' && s2[i] == '.' {
            s2[i - 1] = '.';
            c2 += 1;
        }
    }

    let mut ans = min(c1, c2);

    let mut b: Vec<usize> = vec![0; n];
    b[0] = 1;
    for i in 1..n {
        if s[i] == '#' {
            b[i] = b[i - 1] + 1;
        } else {
            b[i] = b[i - 1];
        }
    }
    let mut w: Vec<usize> = vec![0; n];
    w[n - 1] = 1;
    for i in (0..(n - 1)).rev() {
        if s[i] == '.' {
            w[i] = w[i + 1] + 1;
        } else {
            w[i] = w[i + 1];
        }
    }

    for i in 0..(n - 1) {
        if s[i] == '.' && s[i + 1] == '#' {
            ans = min(ans, b[i] + w[i + 1]);
        }
    }

    return ans.to_string();
}

